/*
 * Cap receive handlers
 */
 void virtio_multihop_caps_rx_handler(void *arg, errval_t success, struct capref cap, uint32_t capid)
{
    // Get the binding state from our argument pointer
    struct virtio_binding *_binding = arg;
    struct virtio_multihop_binding *mb = arg;
    
    
    assert(capid == ((mb->capst).rx_capnum));
    
    // Check if there's an associated error
    // FIXME: how should we report this to the user? at present we just deliver a NULL capref
    if (err_is_fail(success)) {
        DEBUG_ERR(success, "could not send cap over multihop channel");
    }
    
    // Switch on current incoming message
    switch (_binding->rx_msgnum) {
    case virtio_open_response__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).open_response).devframe = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX virtio.open_response\n");
            assert(((_binding->rx_vtbl).open_response) != NULL);
            ((_binding->rx_vtbl).open_response)(_binding, ((_binding->rx_union).open_response).err, ((_binding->rx_union).open_response).devframe);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case virtio_add_call__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).add_call).vring = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX virtio.add_call\n");
            assert(((_binding->rx_vtbl).add_call) != NULL);
            ((_binding->rx_vtbl).add_call)(_binding, ((_binding->rx_union).add_call).vq_id, ((_binding->rx_union).add_call).ndesc, ((_binding->rx_union).add_call).has_buffers, ((_binding->rx_union).add_call).vring);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case virtio_extend_call__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).extend_call).vbuf = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX virtio.extend_call\n");
            assert(((_binding->rx_vtbl).extend_call) != NULL);
            ((_binding->rx_vtbl).extend_call)(_binding, ((_binding->rx_union).extend_call).vq_id, ((_binding->rx_union).extend_call).vbuf);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case virtio_req_response__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).req_response).vring = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX virtio.req_response\n");
            assert(((_binding->rx_vtbl).req_response) != NULL);
            ((_binding->rx_vtbl).req_response)(_binding, ((_binding->rx_union).req_response).err, ((_binding->rx_union).req_response).vring);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


/*
 * Message sender functions
 */
static  errval_t virtio_open_call__multihop_send(struct virtio_binding *_binding, struct event_closure _continuation, uint8_t backend)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and the arguments
    _binding->tx_msgnum = virtio_open_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).open_call).backend = backend;
    FL_DEBUG("multihop TX virtio.open_call\n");
    
    // try to send!
    virtio_open_call__multihop_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

