/*
 * Control functions
 */
static  bool virtio_multihop_can_send(struct virtio_binding *b)
{
    struct virtio_multihop_binding *mb = (struct virtio_multihop_binding *)(b);
    return(((b->tx_msgnum) == 0) && (!multihop_chan_is_window_full(&(mb->chan))));
}

static  errval_t virtio_multihop_register_send(struct virtio_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, virtio_multihop_can_send(b)));
}

static  void virtio_multihop_default_error_handler(struct virtio_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated virtio multihop binding (default handler)");
    abort();
}

static  errval_t virtio_multihop_change_waitset(struct virtio_binding *_binding, struct waitset *ws)
{
    struct virtio_multihop_binding *mb = (void *)(_binding);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // change waitset on multi-hop channel
    return(multihop_chan_change_waitset(&(mb->chan), ws));
}

static  errval_t virtio_multihop_control(struct virtio_binding *_binding, idc_control_t control)
{
    // No control flags supported
    return(SYS_ERR_OK);
}

/*
 * Functions to initialise/destroy the binding state
 */
 void virtio_multihop_init(struct virtio_multihop_binding *mb, struct waitset *waitset)
{
    (mb->b).st = NULL;
    (mb->b).waitset = waitset;
    event_mutex_init(&((mb->b).mutex), waitset);
    (mb->b).can_send = virtio_multihop_can_send;
    (mb->b).register_send = virtio_multihop_register_send;
    (mb->b).error_handler = virtio_multihop_default_error_handler;
    (mb->b).tx_vtbl = virtio_multihop_tx_vtbl;
    memset(&((mb->b).rx_vtbl), 0, sizeof((mb->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((mb->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((mb->b).tx_cont_chanstate));
    (mb->b).tx_msgnum = 0;
    (mb->b).rx_msgnum = 0;
    (mb->b).tx_msg_fragment = 0;
    (mb->b).rx_msg_fragment = 0;
    (mb->b).tx_str_pos = 0;
    (mb->b).rx_str_pos = 0;
    (mb->b).tx_str_len = 0;
    (mb->b).rx_str_len = 0;
    (mb->b).bind_cont = NULL;
    (mb->b).change_waitset = virtio_multihop_change_waitset;
    (mb->b).control = virtio_multihop_control;
    mb->trigger_chan = false;
}

 void virtio_multihop_destroy(struct virtio_multihop_binding *mb)
{
    flounder_support_waitset_chanstate_destroy(&((mb->b).register_chanstate));
    flounder_support_waitset_chanstate_destroy(&((mb->b).tx_cont_chanstate));
    assert(! "NYI!");
}


/*
 * Bind function
 */
static  void virtio_multihop_bind_continuation(void *st, errval_t err, struct multihop_chan *chan)
{
    struct virtio_multihop_binding *mb = st;
    
    if (err_is_ok(err)) {
        // set receive handlers
        multihop_chan_set_receive_handler(&(mb->chan), (struct multihop_receive_handler){  .handler = virtio_multihop_rx_handler,  .arg = st });
        multihop_chan_set_caps_receive_handlers(&(mb->chan), (struct monitor_cap_handlers){  .st = st,  .cap_receive_handler = virtio_multihop_caps_rx_handler });
    } else {
        virtio_multihop_destroy(mb);
    }
    
    ((mb->b).bind_cont)((mb->b).st, err, &(mb->b));
}

 errval_t virtio_multihop_bind(struct virtio_multihop_binding *mb, iref_t iref, virtio_bind_continuation_fn *_continuation, void *st, struct waitset *waitset, idc_bind_flags_t flags)
{
    errval_t err;
    virtio_multihop_init(mb, waitset);
    (mb->b).st = st;
    (mb->b).bind_cont = _continuation;
    err = multihop_chan_bind(&(mb->chan), (struct multihop_bind_continuation){  .handler = virtio_multihop_bind_continuation,  .st = mb }, iref, waitset);
    if (err_is_fail(err)) {
        virtio_multihop_destroy(mb);
    }
    return(err);
}


/*
 * Connect callback for export
 */
 errval_t virtio_multihop_connect_handler(void *st, multihop_vci_t vci)
{
    struct virtio_export *e = st;
    errval_t err;
    
    // allocate storage for binding
    struct virtio_multihop_binding *mb = malloc(sizeof(struct virtio_multihop_binding ));
    if (mb == NULL) {
        return(LIB_ERR_MALLOC_FAIL);
    }
    
    // initialize binding
    struct virtio_binding *_binding = &(mb->b);
    virtio_multihop_init(mb, e->waitset);
    (mb->chan).vci = vci;
    
    // run user's connect handler
    err = ((e->connect_cb)(e->st, _binding));
    if (err_is_fail(err)) {
        return(err);
    }
    
    // set receive handlers
    multihop_chan_set_receive_handler(&(mb->chan), (struct multihop_receive_handler){  .handler = virtio_multihop_rx_handler,  .arg = mb });
    multihop_chan_set_caps_receive_handlers(&(mb->chan), (struct monitor_cap_handlers){  .st = mb,  .cap_receive_handler = virtio_multihop_caps_rx_handler });
    
    // send back bind reply
    multihop_chan_send_bind_reply(&(mb->chan), SYS_ERR_OK, (mb->chan).vci, (mb->b).waitset);
    
    return(err);
}

