        // try next backend
        b->binding = malloc(sizeof(struct xeon_phi_multihop_binding ));
        assert((b->binding) != NULL);
        err = xeon_phi_multihop_bind(b->binding, b->iref, xeon_phi_bind_continuation_direct, b, b->waitset, b->flags);
