static  errval_t xeon_phi_domain_lookup_call__lmp_send(struct xeon_phi_binding *_binding, struct event_closure _continuation, const char *name, size_t length)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = xeon_phi_domain_lookup_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).domain_lookup_call).name = ((char *)(name));
    ((_binding->tx_union).domain_lookup_call).length = length;
    FL_DEBUG("lmp TX xeon_phi.domain_lookup_call\n");
    
    // try to send!
    xeon_phi_domain_lookup_call__lmp_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

