/*
 * Cap send/receive handlers
 */
static  void xeon_phi_ump_cap_rx_handler(void *arg, errval_t success, struct capref cap, uint32_t capid)
{
    // Get the binding state from our argument pointer
    struct xeon_phi_binding *_binding = arg;
    struct xeon_phi_ump_binding *b = arg;
    errval_t err;
    err = SYS_ERR_OK;
    
    assert(capid == (((b->ump_state).capst).rx_capnum));
    
    // Check if there's an associated error
    // FIXME: how should we report this to the user? at present we just deliver a NULL capref
    if (err_is_fail(success)) {
        DEBUG_ERR(success, "error in cap transfer");
    }
    
    // Switch on current incoming message
    switch (_binding->rx_msgnum) {
    case xeon_phi_spawn_with_cap_call__msgnum:
        // Switch on current incoming cap
        switch ((((b->ump_state).capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).spawn_with_cap_call).capability = cap;
            if ((_binding->rx_msg_fragment) == 2) {
                FL_DEBUG("ump RX xeon_phi.spawn_with_cap_call\n");
                assert(((_binding->rx_vtbl).spawn_with_cap_call) != NULL);
                ((_binding->rx_vtbl).spawn_with_cap_call)(_binding, ((_binding->rx_union).spawn_with_cap_call).xid, ((_binding->rx_union).spawn_with_cap_call).core, ((_binding->rx_union).spawn_with_cap_call).cmdline, ((_binding->rx_union).spawn_with_cap_call).length, ((_binding->rx_union).spawn_with_cap_call).flags, ((_binding->rx_union).spawn_with_cap_call).capability);
                _binding->rx_msgnum = 0;
                // register for receive notification
                err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = xeon_phi_ump_rx_handler,  .arg = _binding });
                if (err_is_fail(err)) {
                    (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
                }
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case xeon_phi_chan_open_request_call__msgnum:
        // Switch on current incoming cap
        switch ((((b->ump_state).capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).chan_open_request_call).msgframe = cap;
            if ((_binding->rx_msg_fragment) == 1) {
                FL_DEBUG("ump RX xeon_phi.chan_open_request_call\n");
                assert(((_binding->rx_vtbl).chan_open_request_call) != NULL);
                ((_binding->rx_vtbl).chan_open_request_call)(_binding, ((_binding->rx_union).chan_open_request_call).xphi, ((_binding->rx_union).chan_open_request_call).msgframe, ((_binding->rx_union).chan_open_request_call).type, ((_binding->rx_union).chan_open_request_call).domain, ((_binding->rx_union).chan_open_request_call).usrdata);
                _binding->rx_msgnum = 0;
                // register for receive notification
                err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = xeon_phi_ump_rx_handler,  .arg = _binding });
                if (err_is_fail(err)) {
                    (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
                }
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case xeon_phi_chan_open_call__msgnum:
        // Switch on current incoming cap
        switch ((((b->ump_state).capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).chan_open_call).msgframe = cap;
            if ((_binding->rx_msg_fragment) == 1) {
                FL_DEBUG("ump RX xeon_phi.chan_open_call\n");
                assert(((_binding->rx_vtbl).chan_open_call) != NULL);
                ((_binding->rx_vtbl).chan_open_call)(_binding, ((_binding->rx_union).chan_open_call).domain, ((_binding->rx_union).chan_open_call).usrdata, ((_binding->rx_union).chan_open_call).msgframe, ((_binding->rx_union).chan_open_call).type);
                _binding->rx_msgnum = 0;
                // register for receive notification
                err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = xeon_phi_ump_rx_handler,  .arg = _binding });
                if (err_is_fail(err)) {
                    (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
                }
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


