static  errval_t net_queue_manager_register_buffer__multihop_send(struct net_queue_manager_binding *_binding, struct event_closure _continuation, struct capref buf, struct capref sp, uint64_t queueid, uint64_t slots, uint8_t role)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and the arguments
    _binding->tx_msgnum = net_queue_manager_register_buffer__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).register_buffer).buf = buf;
    ((_binding->tx_union).register_buffer).sp = sp;
    ((_binding->tx_union).register_buffer).queueid = queueid;
    ((_binding->tx_union).register_buffer).slots = slots;
    ((_binding->tx_union).register_buffer).role = role;
    FL_DEBUG("multihop TX net_queue_manager.register_buffer\n");
    
    // try to send!
    net_queue_manager_register_buffer__multihop_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

