 errval_t e10k_bind(iref_t iref, e10k_bind_continuation_fn *_continuation, void *st, struct waitset *waitset, idc_bind_flags_t flags)
{
    // allocate state
    struct flounder_generic_bind_attempt *b = malloc(sizeof(struct flounder_generic_bind_attempt ));
    if (b == NULL) {
        return(LIB_ERR_MALLOC_FAIL);
    }
    
    // fill in binding state
    b->iref = iref;
    b->waitset = waitset;
    b->driver_num = 0;
    b->callback = _continuation;
    b->st = st;
    b->flags = flags;
    
    if (flags & IDC_BIND_FLAG_MULTIHOP) {
        e10k_bind_contination_multihop(b, SYS_ERR_OK, NULL);
    } else {
        e10k_bind_continuation_direct(b, SYS_ERR_OK, NULL);
    }
    
    return(SYS_ERR_OK);
}

 errval_t e10k_connect(struct e10k_frameinfo *_frameinfo, e10k_bind_continuation_fn *_continuation, void *st, struct waitset *ws, idc_bind_flags_t flags)
{
    #ifdef CONFIG_FLOUNDER_BACKEND_UMP
    return(e10k_ump_connect(_frameinfo, _continuation, st, ws, flags));
    #else
    assert(!("UMP backend not enabled!"));
    return(ERR_NOTIMP);
    #endif // CONFIG_FLOUNDER_BACKEND_UMP
}

