                assert(((_binding->rx_vtbl).terminate_queue) != NULL);
                ((_binding->rx_vtbl).terminate_queue)(_binding, ((_binding->rx_union).terminate_queue).id);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
