                FL_DEBUG("ump RX e10k.queue_terminated\n");
                assert(((_binding->rx_vtbl).queue_terminated) != NULL);
                ((_binding->rx_vtbl).queue_terminated)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
