                assert(((_binding->rx_vtbl).filter_unregistered) != NULL);
                ((_binding->rx_vtbl).filter_unregistered)(_binding, ((_binding->rx_union).filter_unregistered).filter, ((_binding->rx_union).filter_unregistered).err);
