/*
 * Cap receive handlers
 */
 void e10k_multihop_caps_rx_handler(void *arg, errval_t success, struct capref cap, uint32_t capid)
{
    // Get the binding state from our argument pointer
    struct e10k_binding *_binding = arg;
    struct e10k_multihop_binding *mb = arg;
    
    
    assert(capid == ((mb->capst).rx_capnum));
    
    // Check if there's an associated error
    // FIXME: how should we report this to the user? at present we just deliver a NULL capref
    if (err_is_fail(success)) {
        DEBUG_ERR(success, "could not send cap over multihop channel");
    }
    
    // Switch on current incoming message
    switch (_binding->rx_msgnum) {
    case e10k_queue_init_data__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).queue_init_data).registers = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX e10k.queue_init_data\n");
            assert(((_binding->rx_vtbl).queue_init_data) != NULL);
            ((_binding->rx_vtbl).queue_init_data)(_binding, ((_binding->rx_union).queue_init_data).registers, ((_binding->rx_union).queue_init_data).macaddr);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    case e10k_register_queue_memory__msgnum:
        // Switch on current incoming cap
        switch (((mb->capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).register_queue_memory).tx = cap;
            break;
        case 1:
            ((_binding->rx_union).register_queue_memory).txhwb = cap;
            break;
        case 2:
            ((_binding->rx_union).register_queue_memory).rx = cap;
            if (mb->trigger_chan) {
                mb->trigger_chan = false;
                _binding->tx_msgnum = 0;
                flounder_support_trigger_chan(&(_binding->tx_cont_chanstate));
                flounder_support_trigger_chan(&(_binding->register_chanstate));
            }
            FL_DEBUG("multihop RX e10k.register_queue_memory\n");
            assert(((_binding->rx_vtbl).register_queue_memory) != NULL);
            ((_binding->rx_vtbl).register_queue_memory)(_binding, ((_binding->rx_union).register_queue_memory).id, ((_binding->rx_union).register_queue_memory).tx, ((_binding->rx_union).register_queue_memory).txhwb, ((_binding->rx_union).register_queue_memory).rx, ((_binding->rx_union).register_queue_memory).rxbufsz, ((_binding->rx_union).register_queue_memory).rxhdrsz, ((_binding->rx_union).register_queue_memory).msix_intvec, ((_binding->rx_union).register_queue_memory).msix_intdest, ((_binding->rx_union).register_queue_memory).use_irq, ((_binding->rx_union).register_queue_memory).use_rsc);
            _binding->rx_msgnum = 0;
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


