/*
 * Error handler
 */
static  void fb_rpc_client_error(struct fb_binding *_binding, errval_t _err)
{
    // get RPC client state pointer
    struct fb_rpc_client *_rpc = _binding->st;
    
    if (_rpc->rpc_in_progress) {
        assert(err_is_fail(_err));
        _rpc->async_error = _err;
        // kick waitset with dummy event
        flounder_support_register(&(_rpc->rpc_waitset), &(_rpc->dummy_chanstate), dummy_event_closure, true);
    } else {
        USER_PANIC_ERR(_err, "async error in RPC");
    }
}


/*
 * Init function
 */
 errval_t fb_rpc_client_init(struct fb_rpc_client *rpc, struct fb_binding *binding)
{
    errval_t _err;
    
    // Setup state of RPC client object
    rpc->b = binding;
    rpc->reply_present = false;
    rpc->rpc_in_progress = false;
    rpc->async_error = SYS_ERR_OK;
    waitset_init(&(rpc->rpc_waitset));
    flounder_support_waitset_chanstate_init(&(rpc->dummy_chanstate));
    rpc->vtbl = fb_rpc_vtbl;
    binding->st = rpc;
    
    // Change waitset on binding
    _err = ((binding->change_waitset)(binding, &(rpc->rpc_waitset)));
    if (err_is_fail(_err)) {
        waitset_destroy(&(rpc->rpc_waitset));
        return(err_push(_err, FLOUNDER_ERR_CHANGE_WAITSET));
    }
    
    // Set RX handlers on binding object for RPCs
    (binding->rx_vtbl).get_framebuffer_response = fb_get_framebuffer__rpc_rx_handler;
    (binding->rx_vtbl).set_videomode_response = fb_set_videomode__rpc_rx_handler;
    (binding->rx_vtbl).get_vesamode_response = fb_get_vesamode__rpc_rx_handler;
    (binding->rx_vtbl).set_vesamode_response = fb_set_vesamode__rpc_rx_handler;
    (binding->rx_vtbl).save_vesastate_response = fb_save_vesastate__rpc_rx_handler;
    (binding->rx_vtbl).restore_vesastate_response = fb_restore_vesastate__rpc_rx_handler;
    (binding->rx_vtbl).vsync_response = fb_vsync__rpc_rx_handler;
    
    // Set error handler on binding object
    binding->error_handler = fb_rpc_client_error;
    
    return(SYS_ERR_OK);
}

